{-# OPTIONS -w #-}
module Hydra.BNFC.AbsHydra where

-- Haskell module generated by the BNF converter


newtype LIdent = LIdent String deriving (Eq,Ord,Show)
newtype HsExpr = HsExpr String deriving (Eq,Ord,Show)
data SigRel =
   SigRel Pattern [Equation]
  deriving (Eq,Ord,Show)

data SigFun =
   SigFun Pattern Expr
  deriving (Eq,Ord,Show)

data Pattern =
   PatWild
 | PatName Qualifier LIdent
 | PatUnit
 | PatPair Pattern Pattern
  deriving (Eq,Ord,Show)

data Qualifier =
   QualEmpty
 | QualFlow
  deriving (Eq,Ord,Show)

data Equation =
   EquEqual Expr Expr
 | EquInit Expr Expr
 | EquLocal LIdent [LIdent]
 | EquConnect LIdent LIdent [LIdent]
 | EquConnectFlow LIdent LIdent [LIdent]
 | EquSigRelApp HsExpr Expr
  deriving (Eq,Ord,Show)

data Expr =
   ExprOr Expr Expr
 | ExprAnd Expr Expr
 | ExprLt Expr Expr
 | ExprLte Expr Expr
 | ExprGt Expr Expr
 | ExprGte Expr Expr
 | ExprAdd Expr Expr
 | ExprSub Expr Expr
 | ExprDiv Expr Expr
 | ExprMul Expr Expr
 | ExprPow Expr Expr
 | ExprNeg Expr
 | ExprApp Expr Expr
 | ExprVar LIdent
 | ExprAnti HsExpr
 | ExprInt Integer
 | ExprReal Double
 | ExprUnit
 | ExprPair Expr Expr
  deriving (Eq,Ord,Show)

